import React, { useState, useEffect } from "react"
import  "./fetchtable.css"
import addToPlayList from './playlist';



const Test = () => {

    const[items, Setitems] = useState([])
    const[searchterm, SetsearchTerm] = useState("Sia")


    

    useEffect(() => {  
          const search = async() => {
            const options = makeOptions("POST", true);

await fetch("https://solidcode.xyz/SolidMusic/api/playlist/get",options)
.then(res => res.json())
.then((result)=> {
    Setitems(result)
  
}
)

}
if(searchterm){
    search()
}

},[searchterm])
const resultMap = items.map(item =>{
    return (
      <>
   
        <tr>  
        <td className="td_image">
          
          <img className="td_image" src = {item.result.header_image_thumbnail_url} ></img>
          <tr>
            <div className="trdiv">
             <button>SPOTIFY BUTTON</button> 
             <div>
               <button><a href={'https://genius.com/'+item.result.url}>LYRICS</a></button> 
            </div>
            </div>
          </tr>
        </td>
      <td className="result_title"> {item.result.title} <tr className="result_artist">{item.result.primary_artist.name}</tr>
      </td>
        <td className="result_artist"> {item.result.title_with_featured} 
       <div className="options_music">
       <button onClick={addToPlayList.bind(this,item)}>*ADD TO PLAYLIST*</button>
           </div>
        </td>
        </tr>
        </>
    )
})
return (

     <> 
        <div className="searchbar">
          <label className="searchbar1">Search Music:</label>
          <input
            className="input"
            value={searchterm}
            onChange={e => SetsearchTerm(e.target.value)}
          />
        </div>

      <br></br>

      <div className="div-table">
          <thead>
          <tr>
          <th scope="col"></th>
             <th scope="col"></th>
             <th scope="col"></th>
        </tr>
        </thead>

          {resultMap}
          
      </div>
   </>
)
}
const makeOptions= (method,addToken,body) =>{
    var opts = {
      method: method,
      headers: {
        "Content-type": "application/json",
        'Accept': 'application/json',
      }
    }
    const getToken = () => {
      return localStorage.getItem('jwtToken')
    }
}
export  {Test}
