const Search = () => {
    const[searchterm, SetsearchTerm] = useState("React")
    const[result, Setresult] = useState([])


    useEffect(() => {
        const search = async() => {
fetch("http://localhost:8080/SolidCode-BackEnd/api/solidMusic/search?q="+searchterm)
.then(res => res.json())
.then((result)=> {
    Setresult(result)
}
)

}
if(searchterm){
    search()
}
const resultMap = result.response.hits.map(result =>{
    return (
        <div>
            <p>{result.result.title}</p>
        </div>
    )
})
},[searchterm])
console.log(result)
return (
<div>
      <div className="ui form">
        <div className="field">
          <label>Search Term</label>
          <input
            className="input"
            value={searchterm}
            onChange={e => SetsearchTerm(e.target.value)}
          />
        </div>
      </div>
    </div>
)
}

export {Search}
