import React, { useState, useEffect } from "react"

const Search = () => {
    console.log("SEARCH")
    const[searchterm, SetsearchTerm] = useState("Sia")
    const[items, setItems] = useState([])


 useEffect(() => {
    const search = async() => { 
        fetch("http://localhost:8080/SolidCode-BackEnd/api/solidMusic/search?q="+searchterm)
.then(res => res.json())
.then((result)=> {
    setItems(result)
   
}
)
console.log("items")
console.log(items)
console.log("response")
console.log(items.response)
console.log("hits:")
console.log(items.response.hits)


}
if(searchterm){
    search()
}

},[searchterm])
const resultMap = items.response.hits.map(item =>{
    return (
        <div>
            <p>{item.result.title}</p>
        </div>
    )
})

console.log(items)
return (
<div>
      <div className="ui form">
        <div className="field">
          <label>Search Term</label>
          <input
            className="input"
            value={searchterm}
            onChange={e => SetsearchTerm(e.target.value)}
          />
        </div>
      </div>
      <div>
      {resultMap}
      </div>
    </div>
)
}

export {Search}
