import React, { useState, useEffect } from "react"
import {button, Breadcrumb, Card, Form, Container, Row, Col,Table} from "react-bootstrap"
import  "./fetchtable.css"

const Search = () => {
    console.log("SEARCH")
    const[searchterm, SetsearchTerm] = useState("Search...")
    const[items, setItems] = useState([])
    const [isLoaded, setIsLoaded] = useState(false);

 useEffect(() => {
    const search = async() => { 
       await fetch("http://localhost:8080/SolidCode-BackEnd/api/solidMusic/search?q="+searchterm)
.then(res => res.json())
.then((result)=> {
    console.log("set result")
    setItems(result)
    console.log("set")
   
}
)
console.log("items")
console.log(items)
console.log("response")
console.log(items.response)
console.log("hits:")
console.log(items.response.hits)
setIsLoaded(true);


}
if(searchterm){
    search()
}

},[searchterm])

const resultMap = items.response.map(item =>{
    if(isLoaded) {
    return (
        <div className="div-table">
        <Table >
        <thead>
          <tr>
          <th scope="col"></th>
             <th scope="col"></th>
             <th scope="col"></th>
        </tr>
        </thead>
        <tbody>
         {items.response.hits.map(item =>
            <>
            
          <tr>  
            <td className="td_image">
              
              <img className="td_image" src = {item.result.header_image_thumbnail_url} ></img>
              <tr>
                <div className="trdiv">
                 <button>SPOTIFY BUTTON</button> 
                 <div>
                   <button><a href={'https://genius.com/'+item.result.url}>LYRICS</a></button> 
                </div>
                </div>
              </tr>
            </td>
          <td className="result_title"> {item.result.title} <tr className="result_artist">{item.result.primary_artist.name}</tr>
          </td>
            <td className="result_artist"> {item.result.title_with_featured} 
           <div className="options_music">*ADD TO PLAYLIST*</div>
            </td>
    
            </tr>
            
           </>
           )}
          </tbody>
          </Table>
          
          </div>
    )
         }
})


console.log(items)
return (
<div>
      <div className="ui form">
        <div className="field">
          <label>Search Term</label>
          <input
            className="input"
            value={searchterm}
            onChange={e => SetsearchTerm(e.target.value)}
          />
        </div>
      </div>
      <div>
      {resultMap}
      </div>
    </div>
)
}

export {Search}
